import './App.css';
import { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Alert } from 'react-bootstrap';
import setTimeout from 'jquery';

function App() {

  const [numero, setNumero] = useState(null);
  const [show, setShow] = useState(false);


  useEffect(() => {
  }, [numero]);


  const handleSubmit = (e) => {
    e.preventDefault();
  }

  var divisores = (numero) => {
    var divisores = [];
    for (var i = 1; i <= numero; i++) {
      if (numero % i === 0) {
        divisores.push(i)
      }
    }
    return divisores.join(" - ");
  }


  const clickeado = () => {
    setNumero(document.getElementById("numero").value);
    setShow(true);
    () => {
      setTimeout(() => { setShow(false) }, 8000)
    }
  }


  return (
    <div className="App">
      <Alert variant="secondary" show={show}>Se han calculado los divisores!</Alert>
      <h1>Divisores</h1>
      <form onSubmit={handleSubmit}>
        <input placeholder="Ingrese un numero" type="number" id="numero" />
        <Button variant="secondary" type="submit" size="lg" onClick={clickeado}>Calcular los Divisores</Button>
      </form>
      <h1>{divisores(numero)}</h1>
    </div>
  );

}
export default App;
