import './App.css';
import { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Alert } from 'react-bootstrap';


function App() {

  const [numero, setNumero] = useState(null);
  const [show, setShow] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
  }


  useEffect(() => {
  }, [numero]);
  useEffect(() => {
    setShow(true);
  }, [handleSubmit()]);


  var divisores = (numero) => {
    var divisores = [];
    for (var i = 1; i <= numero; i++) {
      if (numero % i === 0) {
        divisores.push(i)
      }
    }
    return divisores.join(" - ");
  }

  return (
    <div className="App">
      <Alert variant="success" show={show}>Se han calculado los divisores!</Alert>
      <h1>Divisores</h1>
      <form onSubmit={handleSubmit}>
        <input placeholder="Ingrese un numero" type="number" id="numero" />
        <Button variant="secondary" type="submit" size="lg" onClick={() => setNumero(document.getElementById("numero").value)}>Calcular los Divisores</Button>
      </form>
      <h1>{divisores(numero)}</h1>
    </div>
  );
}

export default App;
